mapper.select:


	    select * from(
	        select /*+INDEX_DESC(book_product SYS_C008434)*/ rownum as rn, bookId, bookName,
	            (select authorName from book_author where book_product.authorId = book_author.authorId) authorName, authorId,
	            (select cateName from book_category where book_product.cateCode = book_category.cateCode) cateName,cateCode, publisher, publeYear, bookPrice, bookDiscount
	        from book_product
	        where 






	        rownum <= #{pageNum} * #{amount} 
        	)
        where rn > (#{pageNum} -1) * #{amount}
