mapper.select:

select bno, title, content, writer, regdate, updatedate, views ,replycnt
from (
select /*+INDEX_DESC(tbl_board pk_board) */
rownum rn, bno, title, content, writer, regdate,
updatedate, views, replycnt from tbl_board
where rownum <= #{pageNum} * #{amount}





)
where rn > (#{pageNum}-1) * #{amount}

mapper.update:
UPDATE tbl_board set
views = views + 1
where bno = #{bno}
